import { Outlet, Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import SideBar from '../components/SideBar';
import styles from '../css/Nav.module.css';
import LoginModal from '../components/LoginModal';
import RegModal from '../components/RegModal';

export default function Nav(){
    let [user,SetUser] = useState("");
    let [openLoginModal,SetOpenLoginModal] = useState(false);
    let [openRegModal,SetOpenRegModal] = useState(false);
    let [chkLoginFlag,SetChkLoginFlag] = useState(false);
    let [chkAdminFlag,SetChkAdminFlag] = useState(false);
    let chkStorageId = sessionStorage.getItem("sid");
    let chkStorageRole = sessionStorage.getItem("srole");
    function logout(){
        SetChkLoginFlag(false);
        SetChkAdminFlag(false);
        sessionStorage.removeItem('sid');
    }
    useEffect(()=>{
        if(chkStorageId != null){
            SetChkLoginFlag(true);
            if(chkStorageRole != nll){
                SetChkAdminFlag(true);
            }
        }
    },[])
    return(
        <div>
            {openLoginModal ? <LoginModal closeModal={SetOpenLoginModal} openReg={SetOpenRegModal} loginFlag={SetChkLoginFlag} adminFlag={SetChkAdminFlag}/> : null}
            {openRegModal ? <RegModal closeModal={SetOpenRegModal}/> : null}
            <div className={styles.screen}>
                <SideBar/>
                <section>
                    <header>
                        <div>
                            {!chkLoginFlag ? <input type="button" onClick={()=>{SetOpenLoginModal(true)}} value="Login"/> : <input type="button" onClick={()=>logout()}value="Logout"/>}
                            <input type="button" onClick={()=>{SetOpenRegModal(true)}} value="Sign Up"/>
                        </div>
                        <h1><Link to="/">Tian Tian</Link></h1>
                        <nav>
                            <ul>
                                <li><Link to="buysell">Buy/Sell</Link></li>
                                <li><Link to="community">Community</Link></li>
                                <li><Link to="contact">Contact Us</Link></li>
                                {chkLoginFlag ? <li><Link to="myprofile">My page</Link></li> : null}
                                {chkAdminFlag ? <li><Link to="admin">Admin</Link></li> : null}
                            </ul>
                        </nav>
                    </header>
                    <Outlet/>
                </section>
            </div>
        </div>
    )
}
